{
  "main_argument_text": "Single fathers often face significant barriers and biases in the court system, resulting in unfair treatment and unequal access to resources, which can be attributed to a deeply ingrained sexist bias that disproportionately disadvantages men in sole custody cases.",
  "structured_analysis": {
    "claims": [
      {"id": 1, "text": "Single fathers are disproportionately disadvantaged in the court system due to sexist bias", "confidence": 0.85},
      {"id": 2, "text": "Courts favor mothers in custody disputes, resulting in unequal access to resources", "confidence": 0.75}
    ],
    "evidence": [
      {"claim_id": 1, "text": "Studies show that mothers are more likely to be awarded primary custody, while fathers are subjected to stereotypes and biases", "type": "empirical"},
      {"claim_id": 2, "text": "Research indicates that courts favor mothers in custody disputes, resulting in unequal access to resources", "type": "empirical"}
    ],
    "warrants": [
      {"claim_id": 1, "text": "Biases in the court system perpetuate sexist stereotypes, resulting in unequal treatment of single fathers", "type": "logical"}
      ],
    "rebuttals": [
      {"target_claim_summary": "None", "text": "No previous opponent arguments to consider"}
    ],
    "confidence_score": 0.8,
    "cognitive_effort": 3
  }

